package 二叉树.将给定二叉树变换为源二叉树的镜像;import 二叉树.TreeNode;public class Solution {    public void Mirror(TreeNode root){        TreeNode node ;        if (root != null){            node = root.left;            root.left=root.right;            root.right=node;            if (root.left!=null){                Mirror(root.left);            }            if (root.right!=null){                Mirror(root.right);            }        }    }}