package 常用排序.归并排序;import java.util.ArrayList;public class Solution {    public static void merge_sort(int a[],int first,int last,int temp[]){        if (first<last){            int middle = (first + last) / 2;            merge_sort(a,first,middle,temp);            merge_sort(a,middle+1,last,temp);            mergeArray(a,first,middle,last,temp);        }    }    public static void mergeArray(int a[],int first,int middle,int end,int temp[]){        int i = first;        int m = middle;        int j = middle+1;        int n = end;        int k = 0;        while (i < m && j < n)            if (a[i]<a[j]) {                a[k] = a[i];                k++;                i++;            }        else {            a[k] = a [j];            k++;            j++;            }        while(i<=m){            temp[k] = a[i];            k++;            i++;        }        while(j<=n){            temp[k] = a[j];            k++;            j++;        }        for(int ii=0;ii<k;ii++){            a[first + ii] = temp[ii];        }    }}