package 斐波那契数列;public class Solution {     public static int Fibonacci(int n) {        int a = 1;        int b = 1;        int v = 0;        if(n==1||n==2)            return 1;        for(int i=3;i<=n;i++){            v=a+b;            a=b;            b=v;        }        return v;    }    public static void main(String[] args) {        int fibonacci = Fibonacci(8);        System.out.println(fibonacci);    }}//    public static void main(String[] args) {//        //斐波那契数列 1 1 2 3 5 8 13//        int i = febonacci(6);//        System.out.println(i);//    }//    public static int febonacci(int i){//        if(i==1 || i==2){//            return 1;//        }else {//            return febonacci(i-1)+febonacci(i-2);//        }//    }//}