package 跳台阶_变态跳台阶;public class Solution {    public int JumpFloor(int target) {        if(target==0)            return 0;        if (target==1)            return 1;        if(target==2)            return 2;        return JumpFloor(target-1)+JumpFloor(target-2);    }    public int JumpFloorII(int target) {        if (target <= 0) {            return -1;        } else if (target == 1) {            return 1;        } else {            return 2 * JumpFloorII(target - 1);        }    }    public static void main(String[] args) {        Solution solution = new Solution();        int i = solution.JumpFloor(2);        System.out.println(i);    }}